package com.utilities.vibal.utilities.activities;import android.content.Intent;import android.os.Bundle;import androidx.appcompat.app.AppCompatActivity;import android.view.Menu;import android.view.MenuItem;import android.view.View;import com.utilities.vibal.utilities.R;import butterknife.ButterKnife;import butterknife.OnClick;public class MainActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        ButterKnife.bind(this);    }    @OnClick({R.id.randomChooser, R.id.cashBoxManager})    public void onViewClicked(View view) {        switch (view.getId()) {            case R.id.randomChooser:                startActivity(new Intent(this,RandomChooserActivity.class));                break;            case R.id.cashBoxManager:                startActivity(new Intent(this,CashBoxManagerActivity.class));                break;        }    }    @Override    public boolean onCreateOptionsMenu(Menu menu) {        getMenuInflater().inflate(R.menu.menu_main, menu);        return true;    }    @Override    public boolean onOptionsItemSelected(MenuItem item) {        switch (item.getItemId()) {            case R.id.action_settings:                return true;            case R.id.action_help:                return true;            case R.id.action_about:                return true;            default:                return super.onOptionsItemSelected(item);        }    }}