package com.utilities.vibal.utilities.ui;import android.content.Intent;import android.os.Bundle;import android.util.Log;import android.view.Menu;import android.view.MenuItem;import android.view.View;import androidx.appcompat.app.AlertDialog;import androidx.appcompat.app.AppCompatActivity;import com.utilities.vibal.utilities.R;import com.utilities.vibal.utilities.ui.cashBoxManager.CashBoxManagerActivity;import com.utilities.vibal.utilities.ui.randomChooser.RandomChooserActivity;import com.utilities.vibal.utilities.util.Util;import java.text.NumberFormat;import java.text.ParseException;import butterknife.ButterKnife;import butterknife.OnClick;public class MainActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        ButterKnife.bind(this);        NumberFormat format = NumberFormat.getInstance();        try {            Log.d("Prueba", "onCreate: " + format.parse("8,2"));            Log.d("Prueba", "onCreate: " + format.parse("8.200"));            Log.d("Prueba", "onCreate: " + format.parse("8,2,4"));        } catch (ParseException e) {            e.printStackTrace();        }    }    @OnClick({R.id.randomChooser, R.id.cashBoxManager})    public void onViewClicked(View view) {        switch (view.getId()) {            case R.id.randomChooser:                startActivity(new Intent(this, RandomChooserActivity.class));                break;            case R.id.cashBoxManager:                startActivity(new Intent(this, CashBoxManagerActivity.class));                break;        }    }    @Override    public boolean onCreateOptionsMenu(Menu menu) {        getMenuInflater().inflate(R.menu.menu_toolbar_main, menu);        return true;    }    @Override    public boolean onOptionsItemSelected(MenuItem item) {        switch (item.getItemId()) {            case R.id.action_main_settings:                return true;            case R.id.action_main_help:                Util.getHelpDialog(this,R.string.appHelpTitle,R.string.appHelpMessage).show();                return true;            case R.id.action_main_about:                showAboutDialog();                return true;            default:                return false;        }    }    private void showAboutDialog() {        AlertDialog.Builder builder = new AlertDialog.Builder(this);        builder.setTitle("About this app")                .setMessage(R.string.aboutApp)                .setPositiveButton(R.string.button_gotIt,null)                .show();    }}