package com.utilities.vibal.utilities.util;import android.app.AlertDialog;import android.content.Context;import android.view.inputmethod.InputMethodManager;import android.widget.EditText;import androidx.annotation.NonNull;import com.utilities.vibal.utilities.R;public class Util {    private static final String TAG = "PruebaUtil";    /**     * Creates an alert dialog showing the help of the activity     * @param context current context     * @param helpTitle title for the help dialog     * @param helpResourceString message of the help dialog     * @return the help dialog     */    public static AlertDialog showHelp(Context context,int helpTitle,int helpResourceString) {        AlertDialog.Builder builder = new AlertDialog.Builder(context);        builder.setTitle(helpTitle);        builder.setMessage(helpResourceString);        builder.setPositiveButton(R.string.button_gotIt, null);        AlertDialog dialog = builder.create();        dialog.setCanceledOnTouchOutside(false);        return dialog;    }    /**     * Shows the keyboard     * @param context current context     * @param editText EditText which will receive the focus     */    public static void showKeyboard(@NonNull Context context, EditText editText){        InputMethodManager input = (InputMethodManager) context.getSystemService(Context.INPUT_METHOD_SERVICE);        editText.postDelayed(new Runnable()        {            @Override            public void run()            {                editText.requestFocus();                input.showSoftInput(editText, 0);            }        }, 100);    }}