package com.utilities.vibal.utilities.util;import android.content.Context;import android.content.Intent;import android.view.inputmethod.InputMethodManager;import android.widget.EditText;import androidx.annotation.NonNull;import androidx.appcompat.app.AlertDialog;import com.utilities.vibal.utilities.R;import com.utilities.vibal.utilities.models.CashBox;import java.text.DecimalFormatSymbols;import java.util.regex.Matcher;import java.util.regex.Pattern;public class Util {    private static final String TAG = "PruebaUtil";    private static final Pattern patternDecimalSeparator = Pattern.compile("\\D");    /**     * Creates an alert dialog showing the help of the activity     *     * @param context            current context     * @param helpTitle          title for the help dialog     * @param helpResourceString message of the help dialog     * @return the help dialog     */    public static AlertDialog getHelpDialog(Context context, int helpTitle, int helpResourceString) {        AlertDialog.Builder builder = new AlertDialog.Builder(context);        builder.setTitle(helpTitle)                .setMessage(helpResourceString)                .setPositiveButton(R.string.button_gotIt, null);        AlertDialog dialog = builder.create();        dialog.setCanceledOnTouchOutside(false);        return dialog;    }    /**     * Shows the keyboard     *     * @param context  current context     * @param editText EditText which will receive the focus     */    public static void showKeyboard(@NonNull Context context, EditText editText) {        InputMethodManager input = (InputMethodManager) context.getSystemService(Context.INPUT_METHOD_SERVICE);        editText.postDelayed(new Runnable() {            @Override            public void run() {                editText.requestFocus();                input.showSoftInput(editText, 0);            }        }, 100);    }    public static Intent getShareIntent(CashBox cashBox) {        Intent shareIntent = new Intent(Intent.ACTION_SEND);        shareIntent.putExtra(Intent.EXTRA_TEXT, cashBox.toString())                .setType("text/plain");        return shareIntent;    }    public static double parseDouble(String str) throws NumberFormatException {        char decimalSeparator = DecimalFormatSymbols.getInstance().getMonetaryDecimalSeparator();        Matcher matcher = patternDecimalSeparator.matcher(str);        StringBuilder modifiedString = new StringBuilder(str);        while (matcher.find()) {            if (str.charAt(matcher.start()) == decimalSeparator)                modifiedString.setCharAt(matcher.start(), '.');            else                throw new NumberFormatException("Invalid symbols");        }        return Double.parseDouble(modifiedString.toString());    }}